mapper.select:
SELECT SeatID, SeatNum, SeatState, ScreenID
FROM tbl_seat
WHERE ScreenID=#{screenID}

SELECT SeatID, SeatNum, SeatState, ScreenID
FROM tbl_seat
WHERE SeatID=#{SeatID}

mapper.update:
UPDATE tbl_seat
SET SeatNum=#{SeatNum}, SeatState=#{SeatState}, ScreenID=#{ScreenID}
WHERE SeatID=#{SeatID}
